import { BASE_URL } from "../../config/config";

export const SIGNUP = "SIGNUP";
export const LOGIN = "LOGIN";
export const LOGOUT = "LOGOUT";
export const AUTHENTICATE = "AUTHENTICATE";

const formData = new FormData();
const expirationDate = new Date(new Date().getTime() + parseInt(36 * 1000));

export const authenticate = (userId, token, expiryTime) => {
  return (dispatch) => {
    dispatch(setLogoutTimer(expiryTime));
    dispatch({
      type: AUTHENTICATE,
      userId: userId,
      token: token,
      expiryTime: expiryTime,
    });
  };
};

export const signUp = (username, email, password) => {
  return async (dispatch) => {
    formData.append("username", username);
    formData.append("email", email);
    formData.append("password", password);
    const response = await fetch(BASE_URL + "signup", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error("Something went Wrong !!");
    }
    const resData = await response.json();

    dispatch({
      type: SIGNUP,
    });
  };
};

export const login = (email, password) => {
  return async (dispatch) => {
    formData.append("email", email);
    formData.append("password", password);

    const response = await fetch(BASE_URL + "login", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error("Something went Wrong !!");
    }

    const resData = await response.json();

    dispatch(authenticate(resData.userId, resData.token, expirationDate));

    saveDataToStorage(resData.token, resData.userId, expirationDate);
  };
};

export const logout = () => {
  clearLogoutTimer();
  localStorage.removeItem("userData");
  return {
    type: LOGOUT,
  };
};

const clearLogoutTimer = () => {
  if (timer) {
    clearTimeout(timer);
  }
};

const setLogoutTimer = (expirationTime) => {
  return (dispatch) => {
    timer = setTimeout(() => {
      dispatch(logout());
    }, expirationTime);
  };
};

const saveDataToStorage = (token, userId, expirationData) => {
  localStorage.setItem(
    "userData",
    JSON.stringify({
      token: token,
      userId: userId,
      expiryDate: expirationData.toISOString(),
    })
  );
};
