import { LOGIN, SIGNUP } from "./actions";

const initialState = {
  username: null,
  userId: null,
  token: null,
};

export const authReducer = (state = initialState, action) => {
  switch (action.type) {
    case SIGNUP:
      return {};

    case LOGIN:
      console.log(action.token);
      return {
        username: action.username,
        userId: action.userId,
        token: action.token,
      };

    default:
      return state;
  }
};
