import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "./Product";
import * as actions from "../../store/product/actions";

const Products = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    const userData = localStorage.getItem("userData");

    if (!userData) {
      console.log("no userdata");
      return;
    }

    const transformedData = JSON.parse(userData);
    const { token, userId, expiryDate } = transformedData;
    const expirationDate = new Date(expiryDate);

    if ((expiryDate <= new Date() || !token, !userId)) {
      console.log(" expiryDate or no token , userID");
      return;
    }

    const expiryTime = expirationDate.getTime() - new Date().getTime();

    dispatch(actions.authenticate(userId, token, expiryTime));
  }, [dispatch]);

  //GET THE FILTRED SEARCH PRODUCTS
  const products = useSelector((state) => state.products.filtred);

  useEffect(() => {
    dispatch(actions.fetchProdcuts());
  }, []);

  if (!products || products.length == 0) {
    return (
      <div className="alert alert-warning m-10">
        <div>
          <h2>Error . Oops you've have encountered an error</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="d-flex m-2 flex-wrap">
      {products.map((product) => {
        return <Product key={product._id} product={product} />;
      })}
    </div>
  );
};

export default Products;
