import React, { useRef } from "react";

import { useForm } from "react-hook-form";

const SignUp = () => {
  const { errors, register, watch, handleSubmit } = useForm();
  const password = useRef({});
  password.current = watch("password", "");

  const submitEventHandler = (data) => {
    console.log(data);
  };

  return (
    <div style={{ maxWidth: 600, margin: 15 }}>
      <form onSubmit={handleSubmit(submitEventHandler)}>
        <div class="form-group mb-2">
          <input
            type="text"
            ref={register({
              required: "Password is required",
              pattern: /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/,
            })}
            class="form-control"
            name="email"
            placeholder="Enter email"
          />
          {errors.email && (
            <p className="alert alert-danger mt-2">{errors.email.message}</p>
          )}
        </div>

        <div class="form-group mb-2">
          <input
            type="password"
            class="form-control"
            ref={register({
              required: "Password is required",
              minLength: {
                value: 6,
                message: "Password must have at least 6 characters",
              },
            })}
            name="password"
            placeholder="Password"
          />
          {errors.password && (
            <p className="alert alert-danger mt-2">{errors.password.message}</p>
          )}
        </div>
        <div class="form-group mb-2">
          <input
            type="password"
            class="form-control"
            ref={register({
              validate: (value) =>
                value === password.current || "The passwords do not match",
            })}
            name="password_repeat"
            placeholder="Password Confirmation"
          />
        </div>
        {errors.password_repeat && (
          <p className="alert alert-danger mt-2">
            {errors.password_repeat.message}
          </p>
        )}
        <button type="submit" class="btn btn-primary">
          SignUp
        </button>
      </form>
    </div>
  );
};

export default SignUp;
